    private void btnGrabarActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnGrabarActionPerformed
        String registro[]=new String[3];
        registro[0]=txtFieldProducto.getText();
        registro[1]=txtFieldPrecio.getText();
        registro[2]=txtFieldCantidad.getText();
        modelo.addRow(registro);
        PalaTabla.producto = txtFieldProducto.getText();
        PalaTabla.precio=txtFieldPrecio.getText();
        PalaTabla.cantidad=txtFieldCantidad.getText();
        PalaTabla.grabar();
        txtFieldProducto.setText("");
        txtFieldPrecio.setText("");
        txtFieldCantidad.setText("");
        txtFieldProducto.requestFocus();
    }//GEN-LAST:event_btnGrabarActionPerformed

