    private void btnBuscarActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnBuscarActionPerformed
        for(int pos=0;pos<PalaTabla.datos.size();pos+=3){
            if(PalaTabla.datos.get(pos).equals(txtFieldProducto.getText()))
            {
                txtFieldProducto.setText(PalaTabla.datos.get(pos).toString());
                txtFieldPrecio.setText(PalaTabla.datos.get(pos+1).toString());
                txtFieldCantidad.setText(PalaTabla.datos.get(pos+2).toString());
            }
        }
    }//GEN-LAST:event_btnBuscarActionPerformed

